
class CSGVertex{constructor(pos,normal,uv){this.pos=pos;if(normal)this.normal=normal;if(uv)this.uv=uv;}
clone(){return new CSGVertex(this.pos.clone(),this.normal&&this.normal.clone(),this.uv&&this.uv.clone(),);}
negate(){this.normal=this.normal&&this.normal.negate();}
interpolate(other,t){let normal=null;if(this.normal&&other.normal){normal=this.normal.clone().lerp(other.normal,t);}
let uv=null;if(this.uv&&other.uv){uv=this.uv.clone().lerp(other.uv,t);}
return new CSGVertex(this.pos.clone().lerp(other.pos,t),normal,uv,);}}